tool.command:
java -classpath \$rackjPath:\$samPath rnaseq.ExonCounter

          #for $mappingFiles in $mapping_selections
            -M ${mappingFiles.mappingMethod_opts.mappingMethod} ${mappingFiles.mappingMethod_opts.mappingFilename}
          #end for

          -GFF ${genome.fields.cgffFilePath}
          -model ${genome.fields.modelFilePath}

          #if str($joinFactor) != "":
            -J ${joinFactor}
          #else
            -J 2
          #end if
          #if str($min) != "":
            -min ${min}
          #else
            -min 8
          #end if
          #if str($identityCutoff) != "":
            -ID ${identityCutoff}
          #else
            -ID 0.95
          #end if

          -exon ${isExon}
          -contain ${isContain}
          -ALL ${isALL}
          -intronic true

          -O ${outputPrefix}

          > ${logFile}

          && mv ${outputPrefix}.intronCount ${intronCountFilename}
